drop database dbms_proj;
# Creating database for the project
create database if not exists dbms_proj;
use dbms_proj;

# Table Creation

# 1. insurance_record

CREATE TABLE IF NOT EXISTS insurance_record (
    insurance_id VARCHAR(10),
    Date_of_issue DATETIME NOT NULL,
    Date_of_expiry DATETIME NOT NULL,
    Amount NUMERIC(6, 2) NOT NULL,
    PRIMARY KEY(insurance_id)
);

    
# 2. Financial_Transactions

CREATE TABLE IF NOT EXISTS Financial_Transactions (
    Trans_ID VARCHAR(14),
    Date DATETIME,
    Amount NUMERIC(10, 2) NOT NULL,
    PRIMARY KEY(Trans_ID)
);

# 3. Disease

CREATE TABLE IF NOT EXISTS Disease (
    Name VARCHAR(22)  NOT NULL,
    Disease_ID VARCHAR(6),
    Dis_description VARCHAR(1000),
    PRIMARY KEY(Disease_ID)
);

# 4. Departments

CREATE TABLE IF NOT EXISTS Departments (
    Dept_ID VARCHAR(6),
    type VARCHAR(100)  NOT NULL,
    PRIMARY KEY(Dept_ID)
);
    
# 5. Stock_of_supplies

CREATE TABLE IF NOT EXISTS Stock_of_supplies (
    Supply_id VARCHAR(14),
    Name VARCHAR(23)  NOT NULL,
    Quantity_available INT,
    Category VARCHAR(39),
    Retail_Price numeric(8,2),
    Discount INT,
    Prescription_Needed VARCHAR(3),
    PRIMARY KEY(Supply_id),
    check(Prescription_Needed in ('YES','NO'))
);

#6. Resources

CREATE TABLE IF NOT EXISTS Resources (
    Res_ID VARCHAR(14),
    Category_ID VARCHAR(4),
    Fees numeric(7,2),
    Due_Paid NUMERIC(7, 2),
    Occupied BOOLEAN,
    Repair_Status VARCHAR(50),
    Cost NUMERIC(8, 2),
    PRIMARY KEY(Res_ID)
);
    
# 7. Prescription

CREATE TABLE IF NOT EXISTS Prescription (
    Prescription_ID VARCHAR(12),
    PageImage VARCHAR(2000)  NOT NULL,
    PRIMARY KEY(Prescription_ID)
);

# 8. Person - insurance_record 

CREATE TABLE IF NOT EXISTS Person (
    U_ID VARCHAR(11) ,
    First_Name VARCHAR(30)  NOT NULL,
    Mid_Name VARCHAR(30) ,
    Last_Name VARCHAR(30)  NOT NULL,
    Gender VARCHAR(10) ,
    Age INT,
    Address VARCHAR(50)  NOT NULL,
    Nationality VARCHAR(21)  NOT NULL,
    mobile_no VARCHAR(12) ,
    email_id VARCHAR(32) NOT NULL,
    insurance_id VARCHAR(10) ,
    PRIMARY KEY(U_ID),
    FOREIGN KEY(insurance_id) REFERENCES insurance_record(insurance_id)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

# 9. Human_resources - Person

CREATE TABLE IF NOT EXISTS Human_resources (
    U_ID VARCHAR(11),
    H_ID VARCHAR(6),
    Job VARCHAR(17),
    Salary NUMERIC(9,2),
    No_of_holiday_avl INT,
    Date_of_join DATETIME,
    PRIMARY KEY(H_ID),
    FOREIGN KEY(U_ID) REFERENCES Person(U_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);
    
# 10. Doctors - Person,Departments

CREATE TABLE IF NOT EXISTS Doctors (
    U_ID VARCHAR(11),
    Doctor_ID VARCHAR(6),
    Dept_ID VARCHAR(10),
    Avl_from time,
    Avl_till time,
    Salary NUMERIC(10, 2),
    No_of_holiday_avl INT,
    Date_of_join DATE,
    experience INT,
    PRIMARY KEY(Doctor_ID),
    FOREIGN KEY(U_ID) REFERENCES Person(U_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    FOREIGN KEY(Dept_ID) REFERENCES Departments(Dept_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);
    
# 11. Treatment - Prescription,Disease,Doctors

CREATE TABLE IF NOT EXISTS Treatment (
    Prescription_ID VARCHAR(12),
    Treatment_ID VARCHAR(11),
    Disease_ID VARCHAR(6),
    Start_time DATE,
    End_time DATE,
    Cost NUMERIC(8, 2),
    Doctor_ID VARCHAR(6),
    PRIMARY KEY(Treatment_ID),
    FOREIGN KEY(Prescription_ID) REFERENCES Prescription(Prescription_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    FOREIGN KEY(Disease_ID) REFERENCES Disease(Disease_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    FOREIGN KEY(Doctor_ID) REFERENCES Doctors(Doctor_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);

# 12. Dead_Patient - Person, Treatment

CREATE TABLE IF NOT EXISTS Dead_Patient (
    U_ID VARCHAR(11),
    Patient_ID VARCHAR(10),
    Treatment_ID VARCHAR(11),
    Date_of_admission DATETIME,
    Date_of_death DATETIME,
    Blood_gp VARCHAR(3),
    PRIMARY KEY(Patient_ID),
    FOREIGN KEY(U_ID) REFERENCES Person(U_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    FOREIGN KEY(Treatment_ID) REFERENCES Treatment(Treatment_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    check(Blood_gp in ('O-','O+','A+','A-','B+','B-','AB+','AB-'))
);

# 13. Patient - Person, Treatment

CREATE TABLE IF NOT EXISTS Patient (
    U_ID VARCHAR(11),
    Patient_ID VARCHAR(10),
    Treatment_ID VARCHAR(11),
    Date_of_admission DATETIME,
    Blood_gp VARCHAR(3),
    PRIMARY KEY(Patient_ID),
    FOREIGN KEY(U_ID) REFERENCES Person(U_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    FOREIGN KEY(Treatment_ID) REFERENCES Treatment(Treatment_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE, 
    check(Blood_gp in ('O-','O+','A+','A-','B+','B-','AB+','AB-'))
);
    
# 14. Organ_Donations - Patient

CREATE TABLE IF NOT EXISTS Organ_Donations (
    Donor_ID VARCHAR(6),
    Patient_ID VARCHAR(11),
    Organ VARCHAR(20),
    Donation_date DATETIME,
    Blood_gp VARCHAR(3),
    PRIMARY KEY(Donor_ID),
    FOREIGN KEY(Patient_ID) REFERENCES Patient(Patient_ID)
    ON DELETE CASCADE
    ON UPDATE CASCADE,
    check(Blood_gp in ('O+','O-','A+','A-','B+','B-','AB+','AB-'))
);



